import React, { useContext } from "react";
import "./Works.css";
import Logo from "../../img/logo.png";
import College from "../../img/College.png";

import Name from "../../img/name.png";
// import Amazon from "../../img/amazon.png";
// import Shopify from "../../img/Shopify.png";
// import Facebook from "../../img/Facebook.png";
import { themeContext } from "../../Context";
import { motion } from "framer-motion";
import { Link } from "react-scroll";
const Works = () => {
	// context
	const theme = useContext(themeContext);
	const darkMode = theme.state.darkMode;

	// transition
	return (
		<div className="works" id="works">
			{/* left side */}
			<div className="w-left">
				<div className="awesome">
					{/* dark Mode */}
					<span style={{ color: darkMode ? "white" : "" }}>
						Works for All these
					</span>
					<span>Brands & Clients</span>
					<spane>
						There are many different brands among my clientele. I aim to keep my
						job <br />
						as diverse as possible, thus I don't <br />
						commit to one field. It keeps me sharp and enables me to <br />
						learn new things.
					</spane>
					<Link to="contact" smooth={true} spy={true}>
						<button className="button s-button">Hire Me</button>
					</Link>
					<div
						className="blur s-blur1"
						style={{ background: "#ABF1FF94" }}
					></div>
				</div>

				{/* right side */}
			</div>
			<div className="w-right">
				<motion.div
					initial={{ rotate: 45 }}
					whileInView={{ rotate: 0 }}
					viewport={{ margin: "-40px" }}
					transition={{ duration: 3.5, type: "spring" }}
					className="w-mainCircle"
				>
					<div className="w-secCircle">
						<img
							className="asssa"
							src={College}
							alt=""
							height={"200px"}
							width={"300px"}
						/>
					</div>
					<div className="w-secCircle">
						<img src={Logo} alt="" />
					</div>
					<div className="w-secCircle">
						<img src={Name} alt="" height={"200px"} width={"200px"} />
					</div>{" "}
					{/* <div className="w-secCircle">
						<img src={Logo} alt="" />
					</div> */}
					{/* <div className="w-secCircle">
						<img src={Logo} alt="" />
					</div> */}
				</motion.div>
				{/* background Circles */}
				<div className="w-backCircle blueCircle"></div>
				<div className="w-backCircle yellowCircle"></div>
			</div>
		</div>
	);
};

export default Works;
